bool Transactions::validateJsonForCreation(const Json::Value &pJson, std::string &err)
{
    if(pJson.isMember("id"))
    {
        if(!validJsonOfField(0, "id", pJson["id"], err, true))
            return false;
    }
    if(pJson.isMember("user_id"))
    {
        if(!validJsonOfField(1, "user_id", pJson["user_id"], err, true))
            return false;
    }
    else
    {
        err="The user_id column cannot be null";
        return false;
    }
    if(pJson.isMember("market_id"))
    {
        if(!validJsonOfField(2, "market_id", pJson["market_id"], err, true))
            return false;
    }
    else
    {
        err="The market_id column cannot be null";
        return false;
    }
    if(pJson.isMember("option_id"))
    {
        if(!validJsonOfField(3, "option_id", pJson["option_id"], err, true))
            return false;
    }
    else
    {
        err="The option_id column cannot be null";
        return false;
    }
    if(pJson.isMember("transaction_type"))
    {
        if(!validJsonOfField(4, "transaction_type", pJson["transaction_type"], err, true))
            return false;
    }
    else
    {
        err="The transaction_type column cannot be null";
        return false;
    }
    if(pJson.isMember("number_of_shares"))
    {
        if(!validJsonOfField(5, "number_of_shares", pJson["number_of_shares"], err, true))
            return false;
    }
    else
    {
        err="The number_of_shares column cannot be null";
        return false;
    }
    if(pJson.isMember("price_per_share"))
    {
        if(!validJsonOfField(6, "price_per_share", pJson["price_per_share"], err, true))
            return false;
    }
    else
    {
        err="The price_per_share column cannot be null";
        return false;
    }
    if(pJson.isMember("created_at"))
    {
        if(!validJsonOfField(7, "created_at", pJson["created_at"], err, true))
            return false;
    }
    return true;
}
